interface Store {
  currentPage : number;
  feeds : NewsFeed[];
}

interface RouteInfo {
  path : string;
  page : View;
}

interface News {
  readonly id : number;
  readonly time_ago : string;
  readonly title : string;
  readonly url : string;
  readonly user : string;
  readonly content : string;
}

interface NewsFeed extends News {
  readonly comments_count : number;
  readonly points : number;
  read? : boolean;
}

interface NewsDetail extends News {
  readonly comments : NewsDetailComment[];
}

interface NewsDetailComment extends News {
  readonly comments : NewsDetailComment[];
  readonly level : number;
}

const ajax : XMLHttpRequest = new XMLHttpRequest();
const NEWS_URL = 'https://api.hnpwa.com/v0/news/1.json';
const CONTENT_URL = 'https://api.hnpwa.com/v0/item/@id.json';
const container : HTMLElement | null = document.getElementById('root');

const store : Store = {
    currentPage : 1,
    feeds : []
};

function applyApiMixins(targetClass : any, baseClasses : any[]) {
  baseClasses.forEach(baseClass => {
    Object.getOwnPropertyNames(baseClass.prototype).forEach(name => {
      const descriptor = Object.getOwnPropertyDescriptor(baseClass.prototype, name);

      if (descriptor) {
        Object.defineProperty(targetClass.prototype, name, descriptor);
      }
    });
  });
}

class Api {
  protected getRequest<AjaxResponseType>(url : string) : AjaxResponseType {
    const ajax = new XMLHttpRequest();
    ajax.open('GET', url, false);
    ajax.send();

    // 경우에 따라서 반환하는 값이 NewsFeed[]일때도, NewsDetail일때도 있는 상황
    return JSON.parse(ajax.response);
  }
}

// 믹스인 방법
class NewsFeedApi {
  getData() : NewsFeed[] {
    return this.getRequest<NewsFeed[]>(NEWS_URL);
  }
}

// 믹스인 방법
class NewsDetailApi {
  getData(id : string) : NewsDetail {
    return this.getRequest<NewsDetail>(CONTENT_URL.replace('@id', id));
  }
}

// 믹스인
interface NewsFeedApi extends Api {}; // TypeScript 컴파일러에게 이 두가지가 합성될것임을 알려주는 코드
interface NewsDetailApi extends Api {};
applyApiMixins(NewsFeedApi, [Api]);
applyApiMixins(NewsDetailApi, [Api]);

class Router {
  // Router class 목적 : hash가 바뀌었을때 해당하는 페이지를 보여준다.
  private routeTable : RouteInfo[]; // 최초 시작시에 path와 page(view) 저장됨.
  private defaultRoute : RouteInfo | null; // 최초 시작시에 NewsFeedView 저장됨.

  constructor() {
    const routePath = location.hash;
    // 해시의 변경을 감지하고 변경되면 함수 실행.
    // window.addEventListener에서의 this context는 Router 인스턴스가 아니고 함수를 실행하는 주체인 이벤트가 됨 
    window.addEventListener('hashchange', this.route.bind(this));
    this.routeTable = [];
    this.defaultRoute = null;
  }

  setDefaultPage = (page : View) : void => {
    this.defaultRoute = {
      path : '',
      page : page
    }
  }

  addRoutePath = (path : string, page : View) : void => {
    this.routeTable.push({
      path : path, // 둘이 같으니까 그냥 path라고만 적어도 됨
      page : page,
    })
  }

  route = () : void => {
    const routePath = location.hash;
     // #만 있으면 그냥 빈 문자열 ''로 나온다.
    if(routePath == '' && this.defaultRoute) {
      // 디폴트 페이지인 NewsFeedView로 이동.
      this.defaultRoute.page.render();
    }

    for (const routeInfo of this.routeTable) {
      if (routePath.indexOf(routeInfo.path) >= 0) {
        routeInfo.page.render();
        break;
      }
    }
  }
}

abstract class View {
  // 원본 템플릿 : 이걸 안해놓으면 예를들어 템플릿 안에 @title이 있고 얘가 replace되면
  // @title이 아니라 그 값으로 대체 되기 때문에 한번 replace되고 나서 부터는 @title을 이용해서 replace 못하는 문제 발생
  private template : string;
  private renderTemplate : string; // 템플릿의 id가 replace될때 사용할 일시적인 템플릿
  private container : HTMLElement;
  private htmlList : string[];

  constructor(containerId : string, template : string) {
    const containerElement = document.getElementById(containerId);
    if (!containerElement) {
      throw "최상위 컨테이너가 없어 UI를 진행하지 못합니다.";
    }
    this.container = containerElement;
    this.template = template;
    this.renderTemplate = template;
    this.htmlList = [];
  }

  protected updateView = () : void => {
    if (this.container) { // type guard
      this.container.innerHTML = this.renderTemplate;
      this.renderTemplate = this.template; // 값이 replace되어 버린 @id들을 다시 원본 템플릿으로 살려놓기 위함
    } else {
      console.log("최상위 컨테이너가 없어 UI를 진행하지 못합니다.");
    }
  }

  protected addHtml = (htmlString : string) : void => {
    this.htmlList.push(htmlString);
  }

  protected getHtml = () : string => {
    const snapshot = this.htmlList.join('')
    this.clearHtmlList();
    return snapshot;
  }

  private clearHtmlList = () : void => {
    this.htmlList = [];
  }

  protected setTemplateData = (key : string, value : string) : void => {
    this.renderTemplate = this.renderTemplate.replace(`@${key}`, value);
  }

  // abstract : 자식 클래스들이 무조건 render를 override하게 만드는 키워드
  // 그리고 이 키워드를 쓰려면 View 클래스 자체에도 abstract 키워드가 붙어야 한다.
  abstract render() : void;
}

class NewsFeedView extends View{
  // 클래스로 뷰를 만드는 이유 : 필요한 것을 저장했다가 재사용해서 쓸 수 있기 때문
  private api : NewsFeedApi;
  private feeds : NewsFeed[];

  constructor(containerId: string) { // container는 즉 root를 의미한다.
    let template = `
      <div class="bg-gray-600 min-h-screen">
        <div class="bg-white text-xl">
          <div class="mx-auto px-4">
            <div class="flex justify-between items-center py-6">
              <div class="flex justify-start">
                <h1 class="font-extrabold">Hacker News</h1>
              </div>
              <div class="items-center justify-end">
                <a href="#/page/@prev_page" class="text-gray-900">
                  Previous
                </a>
                <a href="#/page/@next_page" class="text-gray-900 ml-4">
                  Next
                </a>
              </div>
            </div> 
          </div>
        </div>
        <div class="p-4 text-2xl text-gray-700">
          @news_list        
        </div>
      </div>
    `;
    super(containerId, template);
    this.api = new NewsFeedApi();
    this.feeds = store.feeds;

    if (this.feeds.length == 0) {
      this.feeds = store.feeds = this.api.getData();
      this.makeFirstFeedForReadState();
    }    
  }

  // override render
  render = () : void => {
    store.currentPage = Number(location.hash.substring(7) || 1) // default 처리
    const maxPageNumber = this.feeds.length / 10;
    for (let i = (store.currentPage - 1) * 10 ; i < store.currentPage * 10 ; i++) {
      // 구조 분해 할당 방법
      const { id, title, comments_count, user, points, time_ago, read } = this.feeds[i];
      this.addHtml(`
          <div class="p-6 ${read ? 'bg-green-600' : 'bg-white'} mt-6 rounded-lg shadow-md transition-colors duration-500 hover:bg-green-100">
          <div class="flex">
              <div class="flex-auto">
              <a href="#/detail/${id}">${title}</a>  
              </div>
              <div class="text-center text-sm">
              <div class="w-10 text-white bg-green-300 rounded-lg px-0 py-2">${comments_count}</div>
              </div>
          </div>
          <div class="flex mt-3">
              <div class="grid grid-cols-3 text-sm text-gray-500">
              <div><i class="fas fa-user mr-1"></i>${user}</div>
              <div><i class="fas fa-heart mr-1"></i>${points}</div>
              <div><i class="far fa-clock mr-1"></i>${time_ago}</div>
              </div>  
          </div>
          </div>    
      `);
    }
    this.setTemplateData("news_list", this.getHtml());
    // 템플릿의 앵커 태그 안의 값들이 변경되면서 해당 버튼을 클릭했을때 hash 변경이 감지될 것.
    this.setTemplateData("prev_page", String(store.currentPage - 1 > 1 ? store.currentPage - 1 : 1));
    this.setTemplateData("next_page", String(store.currentPage + 1 > maxPageNumber ? maxPageNumber : store.currentPage + 1));

    this.updateView();
  }

  private makeFirstFeedForReadState = () : void => {
    // 처음 피드 데이터를 받아오면서 read속성을 false값으로 초기화해서 부여하기 위한 함수
    // 기존에는 input값과 output값이 있었는데 이제는 클래스의 내부 메서드가 되었으니까 값들을 그대로 참조할 수 있어서 지워줌
    for (let i = 0 ; i < this.feeds.length ; i++) {
        this.feeds[i].read = false;
    }
  }
}

class NewsDetailView extends View {
  constructor(containerId : string) {
    let template = `
    <div class="bg-gray-600 min-h-screen pb-8">
      <div class="bg-white text-xl">
        <div class="mx-auto px-4">
          <div class="flex justify-between items-center py-6">
            <div class="flex justify-start">
              <h1 class="font-extrabold">Hacker News</h1>
            </div>
            <div class="items-center justify-end">
              <a href="#/page/@current_page" class="text-gray-500">
                <i class="fa fa-times"></i>
              </a>
            </div>
          </div>
        </div>
      </div>

      <div class="h-full border rounded-xl bg-white m-6 p-4 ">
        <h2>@news_content_title</h2>
        <div class="text-gray-400 h-20">
          @news_content_content
        </div>

        @comments

      </div>
    </div>
    `;
    super(containerId, template);
  }

  // override render
  render = () => {
    // 해당 부분은 API가 호출 될때 결정되는 것들 이니까 render함수로 들어오게 됨
    // 앵커태그의 해시가 변경되었을때 이벤트가 발생한다.
    // 해시를 CONTENT_URL의 id란에 넣고 API를 호출해야함
    // 해시를 주소에서 가져와야 하는데 주소 맨끝에 해시가 붙어있으니까 코드는 다음과 같다
    const id = location.hash.substring(9);
    const api = new NewsDetailApi();
    const newsContent = api.getData(id);

    for (let i = 0; i < store.feeds.length ; i++) {
      if (store.feeds[i].id == Number(id)) {
          store.feeds[i].read = true;
          break;
      }
    }
    this.setTemplateData("comments", this.makeComment(newsContent.comments));

    this.setTemplateData('current_page', String(store.currentPage));
    this.setTemplateData('news_content_title', newsContent.title);
    this.setTemplateData('news_content_content', newsContent.content);

    this.updateView();
  }

  private makeComment = (comments : NewsDetailComment[]) : string => {  
    for (let i = 0; i < comments.length ; i++) {
        const comment : NewsDetailComment = comments[i];
        this.addHtml(`
        <div style="padding-left: ${comment.level * 40}px;" class="mt-4">
          <div class="text-gray-400">
            <i class="fa fa-sort-down mr-2"></i>
            <strong>${comment.user}</strong> ${comment.time_ago}
          </div>
          <p class="text-gray-700">${comment.content}</p>
        </div>      
      `);

      if (comment.comments.length > 0) {
          this.addHtml(this.makeComment(comment.comments));
      }
    }

    return this.getHtml();
  }
}

const router : Router = new Router();
const newsFeedView = new NewsFeedView('root');
const newsDetailView = new NewsDetailView('root');

router.setDefaultPage(newsFeedView);
router.addRoutePath('/page/', newsFeedView);
router.addRoutePath('/detail/', newsDetailView);

router.route();
